INSERT INTO `blog_article_copy1` VALUES (1, 'Laravel数据库操作的三种方式', '数据库', 'Laravel提供了3种操作数据库方式：DB facade（原始方式）、查询构造器和Eloquent ORM。', 'uploads/20180615202947202.png', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 数据库的概念\r\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p><p style=\"margin: 0px 0px 16px; padding: 0px; outline: 0px; text-align: justify; color: rgb(79, 79, 79); text-transform: none; line-height: 26px; text-indent: 0px; letter-spacing: normal; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; font-size: 16px; font-style: normal; font-weight: normal; word-spacing: 0px; white-space: normal; -ms-word-break: break-all; box-sizing: border-box; orphans: 2; widows: 2; background-color: rgb(255, 255, 255); font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px;\"><span style=\"outline: 0px; font-size: 14px; -ms-word-break: break-all; box-sizing: border-box;\">一、<span style=\"outline: 0px; font-family: 微软雅黑; -ms-word-break: break-all; box-sizing: border-box;\">数据库操作之</span>DB facade</span></p><p style=\"margin: 0px 0px 16px; padding: 0px; outline: 0px; text-align: justify; color: rgb(79, 79, 79); text-transform: none; line-height: 26px; text-indent: 0px; letter-spacing: normal; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; font-size: 16px; font-style: normal; font-weight: normal; word-spacing: 0px; white-space: normal; -ms-word-break: break-all; box-sizing: border-box; orphans: 2; widows: 2; background-color: rgb(255, 255, 255); font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px;\"><span style=\"outline: 0px; font-size: 14px; -ms-word-break: break-all; box-sizing: border-box;\"><span style=\"outline: 0px; font-family: 微软雅黑; -ms-word-break: break-all; box-sizing: border-box;\">&nbsp; &nbsp;在</span>app-&gt;Http-&gt;Controllers目录下新建一个<span style=\"outline: 0px; font-size: 14px; -ms-word-break: break-all; box-sizing: border-box;\">Student</span>控制器，StudentController.php。</span><span style=\"outline: 0px; font-size: 14px; -ms-word-break: break-all; box-sizing: border-box;\">&nbsp;StudentController.php代码如下：</span></p><p><strong style=\"outline: 0px; font-weight: bold; -ms-word-break: break-all; box-sizing: border-box;\">[php]</strong><span class=\"Apple-converted-space\">&nbsp;</span><a title=\"view plain\" class=\"ViewSource\" style=\"background-position: left top; margin: 0px 10px 0px 0px; padding: 1px; outline: 0px; border: currentColor; border-image: none; width: 16px; height: 16px; color: rgb(103, 149, 181); text-indent: -2000px; font-size: 9px; text-decoration: none; display: inline-block; cursor: pointer; -ms-word-break: break-all; box-sizing: border-box; background-image: url(&quot;https://csdnimg.cn/release/phoenix/images/ico_plain.gif&quot;); background-repeat: no-repeat; background-color: inherit;\" href=\"https://blog.csdn.net/zls986992484/article/details/52824962#\">view plain</a><span style=\"outline: 0px; -ms-word-break: break-all; box-sizing: border-box;\"><span class=\"Apple-converted-space\">&nbsp;</span><a title=\"copy\" class=\"CopyToClipboard\" style=\"background-position: left top; margin: 0px 10px 0px 0px; padding: 1px; outline: 0px; border: currentColor; border-image: none; width: 16px; height: 16px; color: rgb(103, 149, 181); text-indent: -2000px; font-size: 9px; text-decoration: none; display: inline-block; cursor: pointer; -ms-word-break: break-all; box-sizing: border-box; background-image: url(&quot;https://csdnimg.cn/release/phoenix/images/ico_copy.gif&quot;); background-repeat: no-repeat; background-color: inherit;\" href=\"https://blog.csdn.net/zls986992484/article/details/52824962#\">copy</a></span><span style=\"outline: 0px; -ms-word-break: break-all; box-sizing: border-box;\"></span></p><ol class=\"dp-c list-paddingleft-2\" style=\"list-style-type: decimal;\"><li><p><span style=\"margin: 0px; padding: 0px; outline: 0px; border: currentColor; border-image: none; color: black; -ms-word-break: break-all; box-sizing: border-box; background-color: inherit;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; border: currentColor; border-image: none; color: black; -ms-word-break: break-all; box-sizing: border-box; background-color: inherit;\">&lt;?php&nbsp;&nbsp;&nbsp;</span></span></p></li><li><p><span style=\"margin: 0px; padding: 0px; outline: 0px; border: currentColor; border-image: none; color: black; -ms-word-break: break-all; box-sizing: border-box; background-color: inherit;\">namespace&nbsp;App\\Http\\Controllers;&nbsp;&nbsp;</span></p></li><li><p><span style=\"margin: 0px; padding: 0px; outline: 0px; border: currentColor; border-image: none; color: black; -ms-word-break: break-all; box-sizing: border-box; background-color: inherit;\"><span class=\"keyword\" style=\"margin: 0px; padding: 0px; outline: 0px; border: currentColor; border-image: none; color: rgb(0, 102, 153); font-weight: bold; -ms-word-break: break-all; box-sizing: border-box; background-color: inherit;\">use</span><span style=\"margin: 0px; padding: 0px; outline: 0px; border: currentColor; border-image: none; color: black; -ms-word-break: break-all; box-sizing: border-box; background-color: inherit;\">&nbsp;Illuminate\\Support\\Facades\\DB;&nbsp;&nbsp;</span></span></p></li><li><p><span style=\"margin: 0px; padding: 0px; outline: 0px; border: currentColor; border-image: none; color: black; -ms-word-break: break-all; box-sizing: border-box; background-color: inherit;\"><span class=\"keyword\" style=\"margin: 0px; padding: 0px; outline: 0px; border: currentColor; border-image: none; color: rgb(0, 102, 153); font-weight: bold; -ms-word-break: break-all; box-sizing: border-box; background-color: inherit;\">class</span><span style=\"margin: 0px; padding: 0px; outline: 0px; border: currentColor; border-image: none; color: black; -ms-word-break: break-all; box-sizing: border-box; 