-- ----------------------------
-- Table structure for blog_article
-- ----------------------------
DROP TABLE IF EXISTS `blog_article`;
CREATE TABLE `blog_article`  (
  `art_id` int(11) NOT NULL AUTO_INCREMENT,
  `art_title` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '文章标题',
  `art_tag` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '标签',
  `art_discription` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '描述',
  `art_thumb` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'default.jpg' COMMENT '缩略图',
  `art_content` text CHARACTER SET utf8 COLLATE utf8_general_ci COMMENT '内容',
  `art_time` int(11) DEFAULT NULL COMMENT '发布时间',
  `art_author` varchar(11) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '作者',
  `art_view` int(11) NOT NULL DEFAULT 0 COMMENT '查看次数',
  `cate_id` int(11) NOT NULL DEFAULT 0 COMMENT '所属分类',
  PRIMARY KEY (`art_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 7 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

